import React, { Component } from "react";

export default class Sample2 extends Component {
  constructor() {
    super();

    this.state = {
      count: 100,
    };
  }

  myclick = () => {
    this.setState({
      count: this.state.count + 1,
    });
  };

  render() {
    return (
      <div>
        <h1>{this.state.count}</h1>
        <button onClick={this.myclick}>click</button>
      </div>
    );
  }
}
